onEvent('recipes', event => {
    event.shaped('minecraft:diamond', [
        'AAA',
        'AAA',
        'AAA'
    ], {
        A: 'minecraft:iron_ingot'
    })
})

onEvent('player.logged_in', event => {
    if (!event.player.stages.has('starting_items')) {
        event.player.give('minecraft:stone_sword')
        event.player.stages.add('starting_items')
    }
})

onEvent('entity.death', event => {
    if (event.entity.type == 'minecraft:zombie') {
        event.server.runCommandSilent(`summon lightning_bolt ${event.entity.x} ${event.entity.y} ${event.entity.z}`)
    }
})

onEvent('block.right_click', event => {
    if (event.block.id == 'minecraft:diamond_block') {
        event.player.tell('你发现了一个钻石块！')
    }
})
